import axios from "axios";
import React, { useContext, useState } from "react";
import "./DashBoard.css";
import { MyContext } from "./MyContext/ContextApi";

const DashBoard = () => {
  const [url, setUrl] = useState("");
  const [response, setResponse] = useState([]);

  let { data: {data: {user}} } = useContext(MyContext)
  console.log(user)

  const handleUrlSubmit = async (e) => {
    e.preventDefault();
    console.log(url);
    let response = await axios.post(`http://localhost:5000/shorten`, {
      userId: user._id,
      longUrl: url,
    });
    console.log(response)
    let shortUrl = response.data.newCreateUrl;
    setResponse(shortUrl);
  };

  const handleUrlChange = (e) => {
    setUrl(e.target.value);
  };

  return (
    <div className="container">
      <h1>URL Shortener</h1>
      <div className="form_container">
        <form onSubmit={handleUrlSubmit}>
          <input
            type="text"
            placeholder="Enter Url here "
            onChange={handleUrlChange}
            required
          />
          <button type="submit">Submit</button>
        </form>
      </div>
      <table>
        <thead>
          <tr>
            <th>Original URL</th>
            <th>Shortened URL</th>
          </tr>
        </thead>
        <tbody>
          <tr key={response.id}>
            <td>{response.longUrl}</td>
            <td>http://localhost:5000/{response.shortUrl}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default DashBoard;
