import axios from "axios";
import React, { useEffect, useState } from "react";
import "./DashBoard.css";
import { MyContext } from "./MyContext/ContextApi";
import { Link } from "react-router-dom";

const DashBoard = () => {
  const [url, setUrl] = useState("");
  const [response, setResponse] = useState([]);
  console.log(response);

  const getData = async () => {
    try {
      let userId = localStorage.getItem("userId");
      console.log(userId);
      let response = await axios.get(`http://localhost:5000/user/${userId}`);
      console.log(response);
      setResponse(response.data.user.url);
    } catch (err) {
      console.error(err);
    }
  };
  useEffect(() => {
    getData();
  }, []);

  const handleUrlSubmit = async (e) => {
    e.preventDefault();
    // console.log(url);
    let userId = localStorage.getItem("userId");
    let response = await axios.post(`http://localhost:5000/shorten`, {
      userId: userId,
      longUrl: url,
    });
    console.log(response);
    // let shortUrl = response.data.urlData.url;
    // setResponse(shortUrl);
  };

  const handleUrlChange = (e) => {
    setUrl(e.target.value);
  };

  return (
    <div className="container">
      <div>
        <h2>Welcome to URL Shortener!</h2>
        <p>
          Enter a long URL and we will generate a shortened version for you.
          Click on the link to access the shortened URL.
        </p>
        <p>
          Please note that the shortened URL will be valid for 24 hours. If you
          need a new shortened URL, please submit the original URL again.
        </p>
        <p>
          <Link to="/">Back to Home</Link>
        </p>
        <p>
          <Link to="/dashboard">Go to Dashboard</Link>
        </p>
        <p>
          <Link to="/logout">Logout</Link>
        </p>
        <p>User ID: {localStorage.getItem("userId")}</p>
        <p>User Email: {localStorage.getItem("email")}</p>
      </div>
      <h1>URL Shortener</h1>
      <div className="form_container">
        <form onSubmit={handleUrlSubmit}>
          <input
            type="text"
            placeholder="Enter Url here "
            onChange={handleUrlChange}
            required
          />
          <button type="submit">Submit</button>
        </form>
      </div>
      <table>
        <thead>
          <tr>
            <th>Original URL</th>
            <th>Shortened URL</th>
          </tr>
        </thead>
        <tbody>
          {response.map((response) => {
            return (
              <tr key={response._id}>
                <td>{response.longUrl}</td>
                <td>
                  <Link to={`http://localhost:5000/${response.shortUrl}`}>
                    {response.shortUrl}
                  </Link>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default DashBoard;
